#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n, m;
  cin >> n >> m;

  vector<tuple<int, int>> black_cells(m);
  for (int i = 0; i < m; ++i) {
    cin >> get<0>(black_cells[i]) >> get<1>(black_cells[i]);
  }

  vector<vector<bool>> grid(n + 1, vector<bool>(n + 1, false));
  for (int i = 0; i < m; ++i) {
    grid[get<0>(black_cells[i])][get<1>(black_cells[i])] = true;
  }

  bool changed = true;
  while (changed) {
    changed = false;
    for (int x = 1; x <= n; ++x) {
      for (int y = 1; y <= n; ++y) {
        for (int z = 1; z <= n; ++z) {
          if (grid[x][y] && grid[y][z] && !grid[z][x]) {
            grid[z][x] = true;
            changed = true;
          }
        }
      }
    }
  }

  int count = 0;
  for (int i = 1; i <= n; ++i) {
    for (int j = 1; j <= n; ++j) {
      if (grid[i][j]) {
        count++;
      }
    }
  }

  cout << count << endl;
  return 0;
}
