def solve():
    n, m = map(int, input().split())
    black_cells = set()
    for _ in range(m):
        black_cells.add(tuple(map(int, input().split())))

    changed = True
    while changed:
        changed = False
        to_add = set()
        for x, y in black_cells:
            for y1, z in black_cells:
                if y == y1 and (z, x) not in black_cells:
                    to_add.add((z, x))
                    changed = True
        black_cells.update(to_add)

    print(len(black_cells))

solve()
