#include <stdio.h>
#include <string.h>

int main() {
    char s[101];
    scanf("%s", s);
    int c_found = 0, f_found = 0;
    for (int i = 0; i < strlen(s); i++) {
        if (!c_found && s[i] == 'C') {
            c_found = 1;
        } else if (c_found && !f_found && s[i] == 'F') {
            f_found = 1;
        }
    }
    if (c_found && f_found) {
        printf("Yes\n");
    } else {
        printf("No\n");
    }
    return 0;
}
