#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;

  int c_pos = -1;
  int f_pos = -1;

  for (int i = 0; i < s.length(); ++i) {
    if (s[i] == 'C') {
      c_pos = i;
      break;
    }
  }

  for (int i = c_pos + 1; i < s.length(); ++i) {
    if (s[i] == 'F') {
      f_pos = i;
      break;
    }
  }

  if (c_pos != -1 && f_pos != -1) {
    cout << "Yes" << endl;
  } else {
    cout << "No" << endl;
  }

  return 0;
}
