import java.lang.*;

class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        String s = sc.next();
        int c = 0;
        int f = 0;
        for (int i = 0; i < s.length(); i++) {
            if (c == 0 && s.charAt(i) == 'C') {
                c = 1;
            } else if (c == 1 && s.charAt(i) == 'F') {
                f = 1;
            }
        }
        if (c == 1 && f == 1) {
            System.out.println("Yes");
        } else {
            System.out.println("No");
        }
    }
}
