#include <stdio.h>

int main() {
    int K, T;
    scanf("%d %d", &K, &T);
    int a[T];
    int sum = 0;
    for (int i = 0; i < T; i++) {
        scanf("%d", &a[i]);
        sum += a[i];
    }
    if (T == 1) {
        printf("%d\n", K - 1);
        return 0;
    }
    int max = 0;
    for (int i = 0; i < T; i++) {
        if (a[i] > max) {
            max = a[i];
        }
    }
    int ans = max - 1;
    int remain = K - max;
    if (remain > T -1){
        ans += remain - (T-1);
    }

    printf("%d\n", ans);
    return 0;
}
