#include <iostream>
#include <vector>
#include <numeric>
#include <algorithm>

using namespace std;

int main() {
    int k, t;
    cin >> k >> t;

    vector<long long> a(t);
    for (int i = 0; i < t; ++i) {
        cin >> a[i];
    }

    if (t == 1) {
        cout << k - 1 << endl;
        return 0;
    }

    long long min_same_days = -1;

    vector<int> p(k);
    vector<int> counts(t);
    for (int i = 0; i < t; i++) {
        counts[i] = a[i];
    }

    vector<int> indices(t);
    iota(indices.begin(), indices.end(), 0);

    function<void(int)> permute = [&](int index) {
        if (index == k) {
            long long same_days = 0;
            for (int i = 1; i < k; ++i) {
                if (p[i] == p[i - 1]) {
                    same_days++;
                }
            }
            if (min_same_days == -1 || same_days < min_same_days) {
                min_same_days = same_days;
            }
            return;
        }

        for (int i = 0; i < t; ++i) {
            if (counts[i] > 0) {
                p[index] = i;
                counts[i]--;
                permute(index + 1);
                counts[i]++;
            }
        }
    };

    permute(0);
    cout << min_same_days << endl;

    return 0;
}
