#include <stdio.h>

#define MOD 1000000007

int main() {
    int N;
    scanf("%d", &N);
    long long T[N], A[N];
    for (int i = 0; i < N; i++) scanf("%lld", &T[i]);
    for (int i = 0; i < N; i++) scanf("%lld", &A[i]);
    long long min_h[N];
    for (int i = 0; i < N; i++) min_h[i] = (T[i] > A[N - 1 - i] ? T[i] : A[N - 1 - i]);
    long long ans = 1;
    long long cur_max = 0;
    for (int i = 0; i < N; i++) {
        if (min_h[i] < cur_max) {
            printf("0\n");
            return 0;
        }
        long long count = min_h[i] - cur_max;
        long long temp = 1;
        for (long long j = 0; j < count; j++) {
            temp = (temp * (min_h[i] - cur_max - j)) % MOD;
        }
        ans = (ans * temp) % MOD;
        cur_max = min_h[i];
    }
    printf("%lld\n", ans);
    return 0;
}
