#include <iostream>
#include <vector>

using namespace std;

int main() {
  int n;
  cin >> n;

  vector<long long> t(n);
  for (int i = 0; i < n; ++i) {
    cin >> t[i];
  }

  vector<long long> a(n);
  for (int i = 0; i < n; ++i) {
    cin >> a[i];
  }

  long long count = 0;
  vector<long long> h(n);
  function<void(int)> solve = [&](int k) {
    if (k == n) {
      bool ok = true;
      vector<long long> max_t(n), max_a(n);
      max_t[0] = h[0];
      max_a[n - 1] = h[n - 1];
      for (int i = 1; i < n; ++i) {
        max_t[i] = max(max_t[i - 1], h[i]);
      }
      for (int i = n - 2; i >= 0; --i) {
        max_a[i] = max(max_a[i + 1], h[i]);
      }
      for (int i = 0; i < n; ++i) {
        if (max_t[i] != t[i] || max_a[i] != a[i]) {
          ok = false;
          break;
        }
      }
      if (ok) count = (count + 1) % 1000000007;
      return;
    }

    for (long long i = 1; i <= 1000; ++i) {
      h[k] = i;
      solve(k + 1);
    }
  };

  solve(0);
  cout << count << endl;
  return 0;
}
