import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] t = new long[n];
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            t[i] = sc.nextLong();
        }
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextLong();
        }
        long mod = 1000000007;
        long[][] dp = new long[n + 1][1001];
        dp[0][0] = 1;
        for (int i = 0; i < n; i++) {
            for (int j = 1; j <= 1000; j++) {
                if (j <= t[i] && j <= a[n - 1 - i]) {
                    dp[i + 1][j] = (dp[i + 1][j] + dp[i][j]) % mod;
                    if (i > 0) {
                        for (int k = 1; k < j; k++) {
                            dp[i + 1][j] = (dp[i + 1][j] + dp[i][k]) % mod;
                        }
                    }
                }
            }
        }
        long ans = 0;
        for (int i = 1; i <= 1000; i++) {
            ans = (ans + dp[n][i]) % mod;
        }
        System.out.println(ans);
    }
}
