#include <stdio.h>
#include <stdlib.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char c[H][W];
    for (int i = 0; i < H; i++) {
        char s[305];
        scanf("%s", s);
        for (int j = 0; j < W; j++) {
            c[i][j] = s[j];
        }
    }
    long long ans = 0;
    for (int k = 0; k < H * W; k++) {
        int min_cost = 1e9;
        int min_col = -1;
        for (int j = 0; j < W; j++) {
            int cost = 0;
            for (int i = 0; i < H; i++) {
                if (j > 0 && i < H && c[i][j] == c[i][j - 1]) cost++;
                if (j < W - 1 && i < H && c[i][j] == c[i][j + 1]) cost++;
            }
            if (cost < min_cost) {
                min_cost = cost;
                min_col = j;
            }
        }
        ans += min_cost;
        for (int i = H - 1; i > 0; i--) {
            c[i][min_col] = c[i - 1][min_col];
        }
        c[0][min_col] = ' ';

    }
    printf("%lld\n", ans);
    return 0;
}
