import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int H = sc.nextInt();
        int W = sc.nextInt();
        char[][] c = new char[H][W];
        for (int i = 0; i < H; i++) {
            String s = sc.next();
            for (int j = 0; j < W; j++) {
                c[i][j] = s.charAt(j);
            }
        }
        long ans = Long.MAX_VALUE;
        for (int i = 0; i < (1 << (H * W)); i++) {
            long cost = 0;
            char[][] cur = new char[H][W];
            for (int j = 0; j < H; j++) {
                System.arraycopy(c[j], 0, cur[j], 0, W);
            }
            int[] cnt = new int[W];
            int cur_cost = 0;
            for (int j = 0; j < H * W; j++) {
                int col = (i >> j) & 1;
                int idx = j % W;
                if (col == 0) continue;
                if (cnt[idx] >= H) continue;
                int cur_cnt = 0;
                for (int k = 0; k < H; k++) {
                    if (cur[k][idx] == '\0') continue;
                    if (idx > 0 && cur[k][idx] == cur[k][idx - 1]) cur_cnt++;
                    if (idx < W - 1 && cur[k][idx] == cur[k][idx + 1]) cur_cnt++;
                }
                cur_cost += cur_cnt;
                for (int k = H - 1; k > 0; k--) {
                    cur[k][idx] = cur[k - 1][idx];
                }
                cur[0][idx] = '\0';
                cnt[idx]++;
            }
            boolean ok = true;
            for (int j = 0; j < W; j++) {
                if (cnt[j] < H) ok = false;
            }
            if (ok) ans = Math.min(ans, cur_cost);
        }
        System.out.println(ans);
    }
}
