#include <stdio.h>

#include <stdlib.h>

#define MOD 1000000007

long long factorial(int n) {
  long long res = 1;
  for (int i = 2; i <= n; i++) {
    res = (res * i) % MOD;
  }
  return res;
}

int main() {
  int n;
  scanf("%d", &n);
  int p[n];
  int count = 0;
  int used[n + 1];
  for (int i = 0; i <= n; i++) used[i] = 0;
  for (int i = 0; i < n; i++) {
    scanf("%d", &p[i]);
    if (p[i] != 0) {
      used[p[i]] = 1;
      count++;
    }
  }
  long long ans = 0;
  int unused[n - count];
  int k = 0;
  for (int i = 1; i <= n; i++) {
    if (used[i] == 0) {
      unused[k++] = i;
    }
  }
  long long fact = factorial(n - count);
  long long cur = 0;
  long long res = 0;
  do {
    long long temp = 1;
    int cur_p[n];
    int idx = 0;
    for (int i = 0; i < n; i++) {
      if (p[i] == 0) {
        cur_p[i] = unused[idx++];
      } else {
        cur_p[i] = p[i];
      }
    }
    for (int i = 0; i < n; i++) {
      int cnt = 0;
      for (int j = 0; j < i; j++) {
        if (cur_p[j] > cur_p[i]) cnt++;
      }
      temp = (temp + cnt * factorial(n - i - 1)) % MOD;
    }
    res = (res + temp + 1) % MOD;

  } while (next_permutation(unused, unused + n - count));

  printf("%lld\n", res);
  return 0;
}
