#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  long long n;
  cin >> n;

  vector<long long> p(n);
  for (int i = 0; i < n; ++i) {
    cin >> p[i];
  }

  vector<long long> unused;
  vector<bool> used(n + 1, false);
  for (long long x : p) {
    if (x != 0) {
      used[x] = true;
    }
  }
  for (long long i = 1; i <= n; ++i) {
    if (!used[i]) {
      unused.push_back(i);
    }
  }

  long long ans = 0;
  long long mod = 1e9 + 7;
  vector<long long> current_permutation(n);
  vector<long long> permutation(n);
  long long count = 0;

  function<void(long long)> generate_permutations = [&](long long k) {
    if (k == n) {
      count++;
      long long page_num = 1;
      vector<long long> fact(n + 1);
      fact[0] = 1;
      for (long long i = 1; i <= n; i++) {
        fact[i] = (fact[i - 1] * i) % mod;
      }
      vector<long long> perm_copy = permutation;
      for (long long i = 0; i < n; i++) {
        long long pos = 0;
        for (long long j = 0; j < n; j++) {
          if(perm_copy[j] < perm_copy[i] && j < i)
            pos++;
        }
          long long num = perm_copy[i];
          long long cur_fact = fact[n-1-i];
          page_num = (page_num + (pos * cur_fact) % mod) % mod;
      }
        ans = (ans + page_num) % mod;
      return;
    }

    if (p[k] != 0) {
      permutation[k] = p[k];
      generate_permutations(k + 1);
    } else {
      for (long long i = 0; i < unused.size(); ++i) {
        bool used_now = false;
        for(long long j = 0; j < k; ++j){
          if(permutation[j] == unused[i]) used_now = true;
        }
        if (!used_now) {
          permutation[k] = unused[i];
          generate_permutations(k + 1);
        }
      }
    }
  };

  generate_permutations(0);

  cout << ans << endl;
  return 0;
}
