MOD = 10**9 + 7

def factorial(n):
    fact = 1
    for i in range(1, n + 1):
        fact = (fact * i) % MOD
    return fact

def solve():
    n = int(input())
    p = list(map(int, input().split()))

    used = [False] * (n + 1)
    for x in p:
        if x != 0:
            used[x] = True

    unused = []
    for i in range(1, n + 1):
        if not used[i]:
            unused.append(i)

    k = len(unused)

    ans = 0

    import itertools

    for perm_tuple in itertools.permutations(unused):
        perm = list(perm_tuple)
        cur_perm = []
        cur_idx = 0
        for i in range(n):
            if p[i] == 0:
                cur_perm.append(perm[cur_idx])
                cur_idx += 1
            else:
                cur_perm.append(p[i])

        rank = 1

        import copy
        temp_perm = copy.deepcopy(cur_perm)

        for i in range(n):
            count = 0
            for j in range(i + 1, n):
                if temp_perm[j] < temp_perm[i]:
                    count += 1
            rank = (rank + count * factorial(n - i - 1)) % MOD
        ans = (ans + rank) % MOD

    print(ans)

