#include <stdio.h>

int main() {
  int a, b, c;
  scanf("%d %d %d", &a, &b, &c);
  int count = 0;
  int colors[3] = {a, b, c};
  int unique_colors[3];
  int k = 0;
  int is_unique;

  for (int i = 0; i < 3; i++) {
    is_unique = 1;
    for (int j = 0; j < k; j++) {
      if (colors[i] == unique_colors[j]) {
        is_unique = 0;
        break;
      }
    }
    if (is_unique) {
      unique_colors[k] = colors[i];
      k++;
    }
  }
  printf("%d\n", k);
  return 0;
}
