#include <iostream>

int main() {
    long long n, k;
    std::cin >> n >> k;
    if (n == 1) {
        std::cout << k << std::endl;
        return 0;
    }
    long long result = k * (k - 1);
    for (long long i = 2; i < n; ++i) {
        result *= (k - 1);
    }
    std::cout << result << std::endl;
    return 0;
}
