import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int k = scanner.nextInt();

        if (n == 1) {
            System.out.println(k);
            return;
        }

        long[] dp = new long[2];
        dp[0] = k;
        dp[1] = k * (k - 1);

        for (int i = 2; i < n; i++) {
            long next = dp[1] * (k - 1);
            dp[0] = dp[1];
            dp[1] = next;
        }

        System.out.println(dp[1]);
        scanner.close();
    }
}
