#include <stdio.h>

long long gcd(long long a, long long b) {
    if (b == 0) return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    scanf("%d", &n);
    long long t[n], a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &t[i], &a[i]);
    }
    long long ta = t[0], aa = a[0];
    for (int i = 1; i < n; i++) {
        long long g = gcd(ta, aa);
        long long nt = t[i], na = a[i];
        long long k = (ta / g) * nt;
        long long l = (aa / g) * na;
        long long lcm = (ta * nt) / gcd(ta, nt);
        if (k > l){
            long long temp = k/l;
            long long rem = k % l;
            if (rem != 0){
                ta = lcm;
                aa = (aa/g) * na * temp;
            }
            else{
                ta = lcm;
                aa = (aa/g) * na * temp;
            }

        }
        else{
            long long temp = l/k;
            long long rem = l % k;
            if (rem != 0){
                aa = lcm;
                ta = (ta/g) * nt * temp;
            }
            else{
                aa = lcm;
                ta = (ta/g) * nt * temp;
            }
        }

    }
    long long g = gcd(ta, aa);
    printf("%lld\n", ta / g * t[n - 1] + aa / g * a[n - 1]);
    return 0;
}
