#include <iostream>
#include <numeric>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    std::cin >> n;
    long long t[n], a[n];
    for (int i = 0; i < n; ++i)
        std::cin >> t[i] >> a[i];
    long long lcm_ta = a[0];
    for (int i = 1; i < n; ++i) {
        long long g = gcd(a[i - 1], a[i]);
        lcm_ta = lcm_ta * a[i] / g;
    }
    long long total_votes = lcm_ta;
    long long takahashi = total_votes * t[n - 1] / a[n - 1];
    long long aoki = total_votes * a[n-1]/ a[n-1];
    std::cout << takahashi + aoki << std::endl;
    return 0;
}
