import java.util.*;
import java.math.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        long[] t = new long[n];
        long[] a = new long[n];
        for (int i = 0; i < n; i++) {
            t[i] = sc.nextLong();
            a[i] = sc.nextLong();
        }
        long ta = t[0];
        long aa = a[0];
        long g = gcd(ta, aa);
        ta /= g;
        aa /= g;
        long ans = ta + aa;
        for (int i = 1; i < n; i++) {
            long cur_t = t[i];
            long cur_a = a[i];
            long k = (long) Math.ceil((double) ta / cur_t);
            ta = cur_t * k;
            k = (long) Math.ceil((double) aa / cur_a);
            aa = cur_a * k;
            long g2 = gcd(ta, aa);
            ta /= g2;
            aa /= g2;
            ans = ta + aa;

        }

        System.out.println(ans * (long) Math.gcd(t[n-1],a[n-1]));
    }
    static long gcd(long a, long b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }
}
