#include <stdio.h>
#include <string.h>

int main() {
    char s[100001];
    scanf("%s", s);
    int n = strlen(s);
    int rock_count = 0;
    int paper_count = 0;
    int score = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'g') {
            if (rock_count <= paper_count) {
                rock_count++;
            } else {
                score++;
                paper_count++;
            }
        } else {
            score--;
            rock_count++;

        }
    }
    printf("%d\n", score);
    return 0;
}
