#include <stdio.h>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int n;
    scanf("%d", &n);
    long long t[n], a[n];
    for (int i = 0; i < n; i++) {
        scanf("%lld %lld", &t[i], &a[i]);
    }
    long long last_t = t[n - 1];
    long long last_a = a[n - 1];
    long long total = last_t + last_a;
    long long g = gcd(last_t, last_a);
    last_t /= g;
    last_a /= g;

    long long cur_t = t[0];
    long long cur_a = a[0];
    long long cur_g = gcd(cur_t, cur_a);
    cur_t /= cur_g;
    cur_a /= cur_g;

    long long ans = (long long)last_t * cur_a + (long long)last_a * cur_t;

    printf("%lld\n", ans * g);
    return 0;
}
