#include <iostream>
#include <numeric>

long long gcd(long long a, long long b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int N;
    std::cin >> N;
    long long T[N], A[N];
    for (int i = 0; i < N; ++i) {
        std::cin >> T[i] >> A[i];
    }
    long long t = T[N - 1], a = A[N - 1];
    long long g = gcd(t, a);
    t /= g;
    a /= g;
    long long ans = t + a;
    long long cur_t = T[0], cur_a = A[0];
    long long k = 1;
    while (true) {
        bool flag = true;
        for (int i = 0; i < N; ++i) {
            long long g = gcd(T[i], A[i]);
            long long cur_t_i = T[i] / g;
            long long cur_a_i = A[i] / g;
            if (cur_t * cur_a_i > cur_a * cur_t_i) {
                flag = false;
                break;
            }
        }
        if (flag)
            break;
        cur_t++;
        cur_a++;
        k++;
    }
    long long g1 = gcd(cur_t, cur_a);
    cur_t /= g1;
    cur_a /= g1;
    long long total = (cur_t * t) + (cur_a * a);

    long long final_t = T[N-1];
    long long final_a = A[N-1];

    long long k_final = std::max((long long)((double)final_t / cur_t), (long long)((double)final_a / cur_a));

    std::cout << (k_final * cur_t + k_final * cur_a) << std::endl;
    return 0;
}
