import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        sc.close();

        if (n == 1 && m == 1) {
            if (k == 0 || k == 1) {
                System.out.println("Yes");
            } else {
                System.out.println("No");
            }
            return;
        }

        if (k % 2 == (n + m) % 2) {
            System.out.println("Yes");
        } else {
            System.out.println("No");

        }
    }
}
