#include <iostream>
#include <string>

using namespace std;

int main() {
  string s;
  cin >> s;

  long long score = 0;
  long long rock_count = 0;
  long long paper_count = 0;

  for (char c : s) {
    if (c == 'g') {
      if (paper_count < rock_count +1) {
          score++;
          rock_count++;
      } else {
          score--;
          paper_count++;
      }
    } else {
      if (rock_count > paper_count) {
        score--;
        rock_count++;
      } else {
        score++;
        paper_count++;
      }
    }
  }
  cout << score << endl;
  return 0;
}
