import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[][] c = new int[n][4];
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < 4; j++) {
                c[i][j] = sc.nextInt();
            }
        }
        long ans = 0;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                if (i == j) continue;
                for (int k = 0; k < n; k++) {
                    if (k == i || k == j) continue;
                    for (int l = 0; l < n; l++) {
                        if (l == i || l == j || l == k) continue;
                        for (int m = 0; m < n; m++) {
                            if (m == i || m == j || m == k || m == l) continue;
                            for (int o = 0; o < n; o++) {
                                if (o == i || o == j || o == k || o == l || o == m) continue;
                                for (int r1 = 0; r1 < 4; r1++) {
                                    for (int r2 = 0; r2 < 4; r2++) {
                                        for (int r3 = 0; r3 < 4; r3++) {
                                            for (int r4 = 0; r4 < 4; r4++) {
                                                for (int r5 = 0; r5 < 4; r5++) {
                                                    for (int r6 = 0; r6 < 4; r6++) {
                                                        int[] col = new int[8];
                                                        col[0] = c[i][r1];
                                                        col[1] = c[j][(r2 + 1) % 4];
                                                        col[2] = c[k][r3];
                                                        col[3] = c[l][(r4 + 1) % 4];
                                                        col[4] = c[m][r5];
                                                        col[5] = c[o][(r6 + 1) % 4];
                                                        col[6] = c[i][(r1 + 1) % 4];
                                                        col[7] = c[j][r2];
                                                        boolean ok = true;
                                                        for (int p = 0; p < 8; p += 2) {
                                                            if (col[p] != col[p + 1]) {
                                                                ok = false;
                                                                break;
                                                            }
                                                        }
                                                        if (ok) ans++;
                                                    }
                                                }
                                            }
                                        }
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }
        System.out.println(ans / 24);
    }
}
