#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long n, m, k;
  cin >> n >> m >> k;

  vector<tuple<long long, long long>> edges(m);
  for (int i = 0; i < m; ++i) {
    cin >> get<0>(edges[i]) >> get<1>(edges[i]);
    get<0>(edges[i])--; 
    get<1>(edges[i])--; 
  }

  long long count = 0;
  vector<long long> colors(m);

  function<void(int)> generate_colorings = [&](int index) {
    if (index == m) {
      count++;
      return;
    }

    for (long long i = 0; i < k; ++i) {
      colors[index] = i;
      generate_colorings(index + 1);
    }
  };

  generate_colorings(0);

  cout << count % 1000000007 << endl;
  return 0;
}
