import java.util.*;

public class Main {
    static final int MOD = 1000000007;

    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int m = sc.nextInt();
        int k = sc.nextInt();
        List<int[]> edges = new ArrayList<>();
        for (int i = 0; i < m; i++) {
            edges.add(new int[]{sc.nextInt() - 1, sc.nextInt() - 1});
        }

        long ans = 0;
        for (long i = 0; i < Math.pow(k, m); i++) {
            long[] colors = new long[m];
            long temp = i;
            for (int j = m - 1; j >= 0; j--) {
                colors[j] = temp % k;
                temp /= k;
            }

            boolean isUnique = true;
            for (int j = 0; j < m; j++) {
                for (int l = 0; l < m; l++) {
                    if (j == l) continue;
                    List<Integer> cycle1 = findCycle(edges, colors, j, l);
                    if (cycle1.size() > 0) {
                        long[] colors2 = Arrays.copyOf(colors, m);
                        circularShift(colors2, cycle1);
                        if (Arrays.equals(colors, colors2)) continue;

                        boolean found = false;
                        for (int p = 0; p < m; p++) {
                            for (int q = 0; q < m; q++) {
                                if (p == q) continue;
                                List<Integer> cycle2 = findCycle(edges, colors2, p, q);
                                if(cycle2.size() > 0){
                                    long[] colors3 = Arrays.copyOf(colors2,m);
                                    circularShift(colors3, cycle2);
                                    if(Arrays.equals(colors, colors3)){
                                        found = true;
                                        break;
                                    }
                                }
                            }
                            if(found) break;
                        }

                        if(!found) isUnique = false;
                        break;
                    }

                }
                if(!isUnique) break;
            }

            if (isUnique) {
                ans++;
            }
        }

        System.out.println(ans % MOD);
        sc.close();
    }
    static List<Integer> findCycle(List<int[]> edges, long[] colors, int start, int end){
        List<Integer> cycle = new ArrayList<>();
        return cycle;
    }

    static void circularShift(long[] colors, List<Integer> cycle) {
        if (cycle.isEmpty()) return;
        long temp = colors[cycle.get(0)];
        for (int i = 0; i < cycle.size() - 1; i++) {
            colors[cycle.get(i)] = colors[cycle.get(i + 1)];
        }
        colors[cycle.get(cycle.size() - 1)] = temp;
    }
}
