#include <stdio.h>
#include <string.h>

int main() {
    int N, A, B;
    char S[100001];
    scanf("%d %d %d", &N, &A, &B);
    scanf("%s", S);
    int passed = 0;
    int overseas_count = 0;
    int overseas_passed = 0;
    for (int i = 0; i < N; i++) {
        if (S[i] == 'a') {
            if (passed < A + B) {
                printf("Yes\n");
                passed++;
            } else {
                printf("No\n");
            }
        } else if (S[i] == 'b') {
            overseas_count++;
            if (passed < A + B && overseas_count <= B) {
                printf("Yes\n");
                passed++;
                overseas_passed++;
            } else {
                printf("No\n");
            }
        } else {
            printf("No\n");
        }
    }
    return 0;
}
