#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
  int n, a, b;
  cin >> n >> a >> b;
  string s;
  cin >> s;

  vector<string> result(n);
  int passed_count = 0;
  int overseas_count = 0;
  vector<int> overseas_passed;

  for (int i = 0; i < n; ++i) {
    if (s[i] == 'a') {
      if (passed_count < a + b) {
        result[i] = "Yes";
        passed_count++;
      } else {
        result[i] = "No";
      }
    } else if (s[i] == 'b') {
      overseas_count++;
      if (passed_count < a + b && overseas_count <=b) {
        result[i] = "Yes";
        passed_count++;
        overseas_passed.push_back(i);
      } else {
        result[i] = "No";
      }
    } else {
      result[i] = "No";
    }
  }

  for (int i = n - 1; i >= 0; --i) {
    cout << result[i] << endl;
  }

  return 0;
}
