import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int a = sc.nextInt();
        int b = sc.nextInt();
        String s = sc.next();
        int passed = 0;
        int overseasRank = 0;
        List<Integer> overseasIndices = new ArrayList<>();
        for (int i = 0; i < n; i++) {
            char c = s.charAt(i);
            if (c == 'a') {
                if (passed < a + b) {
                    System.out.println("Yes");
                    passed++;
                } else {
                    System.out.println("No");
                }
            } else if (c == 'b') {
                overseasRank++;
                overseasIndices.add(i);
                if (passed < a + b && overseasRank <= b) {
                    System.out.println("Yes");
                    passed++;
                } else {
                    System.out.println("No");
                }
            } else {
                System.out.println("No");
            }
        }
        sc.close();
    }
}
