#include <stdio.h>
#include <stdlib.h>

int main() {
    long long W, H;
    scanf("%lld %lld", &W, &H);
    long long *p = (long long *)malloc(W * sizeof(long long));
    long long *q = (long long *)malloc(H * sizeof(long long));
    long long total_cost = 0;
    for (long long i = 0; i < W; i++) {
        scanf("%lld", &p[i]);
    }
    for (long long i = 0; i < H; i++) {
        scanf("%lld", &q[i]);
    }
    for (long long i = 0; i < W; i++) {
        total_cost += p[i];
    }
    for (long long i = 0; i < H; i++) {
        total_cost += q[i];
    }
    printf("%lld\n", total_cost);
    free(p);
    free(q);
    return 0;
}
