#include <stdio.h>
#include <string.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    int count[26] = {0};
    char a[H][W];
    for (int i = 0; i < H; i++) {
        scanf("%s", a[i]);
        for (int j = 0; j < W; j++) {
            count[a[i][j] - 'a']++;
        }
    }
    int odd_count = 0;
    for (int i = 0; i < 26; i++) {
        if (count[i] % 2 != 0) {
            odd_count++;
        }
    }
    if (H % 2 == 0 && W % 2 == 0) {
        if (odd_count == 0) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    } else if (H % 2 == 0 && W % 2 != 0) {
        if (odd_count <= W) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    } else if (H % 2 != 0 && W % 2 == 0) {
        if (odd_count <= H) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    } else {
        if (odd_count <= 1) {
            printf("Yes\n");
        } else {
            printf("No\n");
        }
    }
    return 0;
}
