#include <iostream>
#include <vector>

using namespace std;

int main() {
  long long w, h;
  cin >> w >> h;

  vector<long long> p(w);
  for (int i = 0; i < w; ++i) {
    cin >> p[i];
  }

  vector<long long> q(h);
  for (int i = 0; i < h; ++i) {
    cin >> q[i];
  }

  long long total_cost = 0;
  for (int i = 0; i < w; ++i) {
    total_cost += p[i];
  }
  for (int i = 0; i < h; ++i) {
    total_cost += q[i];
  }

  cout << total_cost << endl;
  return 0;
}
