#include <stdio.h>

int main() {
    int N;
    scanf("%d", &N);
    long long A[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld", &A[i]);
    }
    long long ans = 0;
    for (int i = 0; i < N; i++) {
        long long min_val = A[i];
        for (int j = i + 1; j < N; j++) {
            min_val = (min_val < A[j] - ans) ? min_val : A[j] - ans;

        }
        if (min_val > 0)
             ans += min_val;
        else break;
    }
    printf("%lld\n", ans);
    return 0;
}
