import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int[] a = new int[n];
        for (int i = 0; i < n; i++) {
            a[i] = sc.nextInt();
        }
        int count = 0;
        for (int i = 0; i < n; i++) {
            int min = a[i];
            for (int j = i + 1; j < n; j++) {
                min = Math.min(min, a[j]);
            }
            if (min > 1) {
                count += min -1;
                a[i] -= min -1;

            } else if (min ==1){
                count++;
                a[i] -=1;
            }

        }
        System.out.println(count);
    }
}
