#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int cmp(const void *a, const void *b) {
    char *str1 = *(char **)a;
    char *str2 = *(char **)b;
    return strcmp(str1, str2);
}

int main() {
    int n, q;
    scanf("%d", &n);
    char **strings = (char **)malloc(n * sizeof(char *));
    for (int i = 0; i < n; i++) {
        strings[i] = (char *)malloc(400001 * sizeof(char));
        scanf("%s", strings[i]);
    }
    scanf("%d", &q);
    for (int i = 0; i < q; i++) {
        int k;
        char p[27];
        scanf("%d %s", &k, p);
        char order[26];
        for (int j = 0; j < 26; j++) {
            order[p[j] - 'a'] = j + 'a';
        }
        char **sorted = (char **)malloc(n * sizeof(char *));
        for (int j = 0; j < n; j++) {
            sorted[j] = (char *)malloc(400001 * sizeof(char));
            strcpy(sorted[j], strings[j]);
        }
        for (int j = 0; j < n; j++) {
            for (int l = 0; sorted[j][l] != '\0'; l++) {
                sorted[j][l] = order[sorted[j][l] - 'a'];
            }
        }
        qsort(sorted, n, sizeof(char *), cmp);
        int rank = 0;
        for (int j = 0; j < n; j++) {
            if (strcmp(sorted[j], strings[k - 1]) == 0) {
                rank = j + 1;
                break;
            }
        }
        printf("%d\n", rank);
        for (int j = 0; j < n; j++) {
            free(sorted[j]);
        }
        free(sorted);
    }
    for (int i = 0; i < n; i++) {
        free(strings[i]);
    }
    free(strings);
    return 0;
}
