import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String[] s = new String[n];
        for (int i = 0; i < n; i++) {
            s[i] = sc.next();
        }
        int q = sc.nextInt();
        for (int i = 0; i < q; i++) {
            int k = sc.nextInt() - 1;
            String p = sc.next();
            char[] order = p.toCharArray();
            Map<Character, Integer> map = new HashMap<>();
            for (int j = 0; j < 26; j++) {
                map.put(order[j], j);
            }
            String[] sorted = Arrays.copyOf(s, n);
            Arrays.sort(sorted, (a, b) -> {
                int len = Math.min(a.length(), b.length());
                for (int j = 0; j < len; j++) {
                    int diff = map.get(a.charAt(j)) - map.get(b.charAt(j));
                    if (diff != 0) {
                        return diff;
                    }
                }
                return a.length() - b.length();
            });
            int rank = 0;
            for (int j = 0; j < n; j++) {
                if (sorted[j].equals(s[k])) {
                    rank = j + 1;
                    break;
                }
            }
            System.out.println(rank);
        }
    }
}
