def solve():
    n = int(input())
    s = [input() for _ in range(n)]
    q = int(input())

    for _ in range(q):
        k, p = input().split()
        k = int(k) - 1

        order = {}
        for i, char in enumerate(p):
            order[char] = i

        def custom_sort(x):
            return "".join([str(order[c]) for c in x])

        sorted_s = sorted(s, key=custom_sort)
        print(sorted_s.index(s[k]) + 1)

solve()
