#include <stdio.h>

int main() {
    int N, A, B;
    scanf("%d %d %d", &N, &A, &B);
    int count = 0;
    for (int i = 0; i < N; i++) {
        int t;
        scanf("%d", &t);
        if (t < A || t >= B) {
            count++;
        }
    }
    printf("%d\n", count);
    return 0;
}
