#include <iostream>
#include <string>
#include <map>

using namespace std;

int main() {
    int h, w;
    cin >> h >> w;
    map<char, int> counts;
    for (int i = 0; i < h; ++i) {
        string s;
        cin >> s;
        for (char c : s) {
            counts[c]++;
        }
    }
    int odd_count = 0;
    for (auto const& [key, val] : counts) {
        if (val % 2 != 0) {
            odd_count++;
        }
    }
    if (h % 2 == 0 && w % 2 == 0) {
        if (odd_count == 0) {
            cout << "Yes" << endl;
        } else {
            cout << "No" << endl;
        }
    } else if (h % 2 == 0 && w % 2 != 0) {
        if (odd_count <= w) {
            cout << "Yes" << endl;
        } else {
            cout << "No" << endl;
        }
    } else if (h % 2 != 0 && w % 2 == 0) {
        if (odd_count <= h) {
            cout << "Yes" << endl;
        } else {
            cout << "No" << endl;
        }
    } else {
        if (odd_count <= 1) {
            cout << "Yes" << endl;
        } else {
            cout << "No" << endl;
        }
    }
    return 0;
}
