#include <stdio.h>
#include <string.h>

int main() {
    int N, K;
    scanf("%d %d", &N, &K);
    char P[N][11];
    int count[26] = {0};
    for (int i = 0; i < N; i++) {
        scanf("%s", P[i]);
        count[P[i][0] - 'A']++;
    }
    int num_kupc = 0;
    while (1) {
        int kupc_size = 0;
        int used[26] = {0};
        for (int i = 0; i < N; i++) {
            int first_char = P[i][0] - 'A';
            if (count[first_char] > 0 && used[first_char] == 0) {
                kupc_size++;
                used[first_char] = 1;
                count[first_char]--;
            }
        }
        if (kupc_size >= K) {
            num_kupc++;
        } else {
            break;
        }
    }
    printf("%d\n", num_kupc);
    return 0;
}
