import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        int k = sc.nextInt();
        sc.nextLine();
        String[] p = new String[n];
        for (int i = 0; i < n; i++) {
            p[i] = sc.nextLine();
        }
        int count = 0;
        Set<String> used = new HashSet<>();
        while (true) {
            Set<Character> firstChars = new HashSet<>();
            int numProblems = 0;
            List<String> problems = new ArrayList<>();
            for (int i = 0; i < n; i++) {
                if (!used.contains(p[i])) {
                    char firstChar = p[i].charAt(0);
                    if (!firstChars.contains(firstChar)) {
                        firstChars.add(firstChar);
                        used.add(p[i]);
                        numProblems++;
                        problems.add(p[i]);
                        if (numProblems == k) break;
                    }
                }
            }
            if (numProblems == k) {
                count++;
            } else {
                break;
            }
        }
        System.out.println(count);
    }
}
