#include <stdio.h>

int main() {
    int T;
    scanf("%d", &T);
    while (T--) {
        int N, D;
        scanf("%d %d", &N, &D);
        if (N == 1) {
            printf("%d\n", D);
        } else {
            printf("%d\n", (N - 1) * 127 + D);
        }
    }
    return 0;
}
