#include <iostream>
#include <vector>
#include <numeric>

using namespace std;

int main() {
  int t;
  cin >> t;

  while (t--) {
    long long n, d;
    cin >> n >> d;

    if (n == 1) {
      cout << d << endl;
      continue;
    }

    long long max_sum = 0;
    if (n == 2) {
        max_sum = 255;
    } else if (n == 3) {
        max_sum = 255;
    } else if (n == 4) {
        max_sum = 400;
    } else {
        max_sum = 0;
        for (long long i = 0; i <= 127; ++i) {
            long long sum = 0;
            long long cur = d;
            vector<long long> cookies = {d};

            long long cur_n = 1;
            while(cur_n < n){
                long long y = i;
                long long new_cookie1 = y;
                long long new_cookie2 = cur ^ y;
                cookies.push_back(new_cookie1);
                cookies.push_back(new_cookie2);
                cur = new_cookie2;
                cur_n += 1;
            }
            sum = accumulate(cookies.begin(), cookies.end(), 0LL);
            max_sum = max(max_sum, sum);

        }
    }
    cout << max_sum << endl;
  }
  return 0;
}
