from collections import Counter

H, W = map(int, input().split())
count = Counter()
for _ in range(H):
    s = input()
    for c in s:
        count[c] += 1

odd_count = 0
for c in count:
    if count[c] % 2 != 0:
        odd_count += 1

if H % 2 == 0 and W % 2 == 0:
    if odd_count == 0:
        print('Yes')
    else:
        print('No')
elif H % 2 == 1 and W % 2 == 1:
    if odd_count <= 1:
        print('Yes')
    else:
        print('No')
elif H % 2 == 1 and W % 2 == 0:
    if odd_count == 0:
        print('Yes')
    else:
        print('No')
else:
    if odd_count == 0:
        print('Yes')
    else:
        print('No')

