#include <iostream>
#include <string>

using namespace std;

int main() {
    int n;
    cin >> n;

    for (int i = 1; i <= n; ++i) {
        string s1 = "", s2 = "";
        for (int j = 0; j < i; ++j) {
            s1 += ".";
            s2 += ".";
        }
        cout << s1 << endl << s2 << endl;
        fflush(stdout);
        string r;
        cin >> r;
        if (r == "end") return 0;

        if (r == "T"){
            for (int k = i + 1; k <=n; k++){
                string s1_ext = s1;
                string s2_ext = s2;
                for (int z = 0; z < k - i; z++){
                    s1_ext += ".";
                    s2_ext += ".";
                }
                cout << s1_ext << endl << s2_ext << endl;
                fflush(stdout);
                string r_ext;
                cin >> r_ext;
                if (r_ext == "end") return 0;
                if (r_ext == "T") i = k;
            }
        }

    }

    return 0;
}
