#include <stdio.h>
#include <stdlib.h>

int main() {
    int H, W;
    scanf("%d %d", &H, &W);
    char grid[H][W];
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            scanf(" %c", &grid[i][j]);
        }
    }
    int count = 0;
    for (int i = 0; i < H; i++) {
        for (int j = 0; j < W; j++) {
            if (grid[i][j] == '.') {
                if (i == 0 || i == H - 1 || j == 0 || j == W - 1)
                    count++;
            }
        }
    }
    printf("%d\n", count);
    return 0;
}
