#include <iostream>
#include <vector>

using namespace std;

int main() {
  int h, w;
  cin >> h >> w;

  vector<string> grid(h);
  for (int i = 0; i < h; ++i) {
    cin >> grid[i];
  }

  int count = 0;
  vector<vector<bool>> visited(h, vector<bool>(w, false));
  vector<pair<int, int>> q;

  for (int i = 0; i < h; ++i) {
    for (int j = 0; j < w; ++j) {
      if (grid[i][j] == 'X' && !visited[i][j]) {
        q.push_back({i, j});
        visited[i][j] = true;
        bool needs_fence = false;

        vector<pair<int,int>> q2 = {q.back()};
        q.pop_back();
        int head = 0;
        while(head < q2.size()){
          pair<int,int> curr = q2[head++];
          int r = curr.first;
          int c = curr.second;
          if(r == 0 || r == h-1 || c == 0 || c == w-1){
            needs_fence = true;
            break;
          }
          int dr[] = {-1,1,0,0};
          int dc[] = {0,0,-1,1};
          for(int k=0; k<4; ++k){
            int nr = r + dr[k];
            int nc = c + dc[k];
            if(nr >=0 && nr < h && nc >= 0 && nc < w && grid[nr][nc] == 'X' && !visited[nr][nc]){
              visited[nr][nc] = true;
              q2.push_back({nr,nc});
            }
          }
        }
        if(needs_fence) count++;
      }
    }
  }

  cout << count << endl;
  return 0;
}
